pqevent.arguments:
userRGBPoints = [0, 0, 0, 0, 1, 1, 1, 1]
userSettingsString = """
{ 
  "array_lookup_tables" : {
    "TCoords" : {
      "PVLookupTable" : {
        "ColorSpace" : 0,
        "NanColor" : [ 1, 0, 0 ],
        "RGBPoints" : %s
      }
    }
  }
}
""" % userRGBPoints

siteRGBPoints = [0, 1, 0.835, 1,
                 0.5, 0.0235, 0.141, 0.5,
                 0.5, 0.0157, 0.137, 0.494,
                 1.0, 0.984, 0.765, 0.0]
siteSettingsString = """
{
   "array_lookup_tables" : {
      "TCoords" : {
         "PVLookupTable" : {
            "ColorSpace" : 0,
            "NanColor" : [ 0, 1, 0 ],
            "RGBPoints" : %s
         }
      }
   }
}
""" % siteRGBPoints

session = paraview.servermanager.vtkSMSession()
pxm = session.GetSessionProxyManager()
settings = paraview.servermanager.vtkSMSettings.GetInstance()
settings.ClearAllSettings()

# Add user and site settings in the way that ParaView does when starting up
settings.AddCollectionFromString(userSettingsString, paraview.vtk.VTK_DOUBLE_MAX)
settings.AddCollectionFromString(siteSettingsString, 1.0)

tfManager = paraview.servermanager.vtkSMTransferFunctionManager()
tf = tfManager.GetColorTransferFunction("TCoords", pxm)

rgbPoints = tf.GetProperty("RGBPoints")

# The transfer function should have the values from the user settings string
# at this point.
try:
  n = min(len(userRGBPoints), rgbPoints.GetNumberOfElements())
  for i in range(n):
    assert(userRGBPoints[i] == rgbPoints.GetElement(i))
except:
  print("Er""ror: RGBPoints mismatch in user settings!")
  for i in range(n):
    print("%d: expected (%f), got (%f)" % (i, userRGBPoints[i], rgbPoints.GetElement(i)))
