# Create a dummy dataset of points, setting metadata on a vector array. And test unicode chars in this field à¦¸. import math
data = self.GetPolyDataOutput() verts = vtk.vtkCellArray() pts = vtk.vtkPoints() scalars = vtk.vtkFloatArray() scalars.SetNumberOfComponents(3) scalars.SetName('Vector Data') scalars.SetComponentName(0, 'The first component') scalars.SetComponentName(1, 'The second component') scalars.SetComponentName(2, 'The third component')
numPts = 100; pts.SetNumberOfPoints(numPts); scalars.SetNumberOfTuples(numPts);
for i in range(numPts): point = [math.sin(i * numPts / (2 * math.pi)), math.cos(i * numPts / (2 * math.pi)), math.tan(i * numPts / (2 * math.pi)) % 1.] scalar = [-point[0], -point[1], -point[2]] pts.SetPoint(i, point) scalars.SetTuple(i, scalar) verts.InsertNextCell(1, [i])
data.SetVerts(verts) data.SetPoints(pts) data.GetPointData().SetScalars(scalars)
