static
void file_delete_entry(bfile_t *fileptr)
{
int idx = fileptr->self;

FILE_LOCK();

Free(fileptr);

_fileList[idx].next = _fileAvail;
_fileList[idx].ptr  = 0;
_fileAvail   	      = &_fileList[idx];

FILE_UNLOCK();

if ( FILE_Debug )
    Message("Removed idx %d from file list", idx);
}


