static
double grid_calc_increment(size_t size, const double *vals)
{
if ( size > 1 )
    {
    double inc = (vals[size-1] - vals[0])/(size-1);
    double abs_inc = fabs(inc);
    for ( size_t i = 1; i < size; ++i )
        if ( fabs(fabs(vals[i-1] - vals[i]) - abs_inc) > 0.01*abs_inc )
        {
            inc = 0;
            break;
        }

    return inc;
    }

return 0;
}


