int getByteswap(int byteorder)
{
int byteswap = -1;

switch (byteorder)
    {
    case CDI_BIGENDIAN:
    case CDI_LITTLEENDIAN:
    case CDI_PDPENDIAN:
    byteswap = (HOST_ENDIANNESS != byteorder);
    break;
    case -1:
    break;
    default:
    Error("unexpected byteorder %d query!", byteorder);
    }

return byteswap;
}


